import * as React from 'react';
import { useIsTransitionDisabled } from './hooks/useIsTransitionDisabled';
import { TOGGLE_TRANSITION_DURATION, StyledDarkModeToggle } from './style';

export type Mode = 'dark' | 'light';

export type Size = 'sm' | 'md' | 'lg';

export interface StyledProps {
  size?: Size;
  inactiveLabelColor?: string;
  inactiveLabelColorOnHover?: string;
  inactiveLabelColorOnActive?: string;
  activeLabelColor?: string;
  activeLabelColorOnHover?: string;
  activeLabelColorOnActive?: string;
  inactiveTrackColor?: string;
  inactiveTrackColorOnHover?: string;
  inactiveTrackColorOnActive?: string;
  activeTrackColor?: string;
  activeTrackColorOnHover?: string;
  activeTrackColorOnActive?: string;
  inactiveThumbColor?: string;
  activeThumbColor?: string;
  focusRingColor?: string;
}

export interface AccessibilityProps {
  ariaLabel?: string;
}

export interface Props extends StyledProps, AccessibilityProps {
  mode?: Mode;
  dark?: string;
  light?: string;
  onChange?: (mode: Mode) => void;
}

export function DarkModeToggle({
  mode = 'dark',
  dark,
  light,
  onChange,
  size = 'sm',
  inactiveLabelColor = '#b9bdc1',
  inactiveLabelColorOnHover = '#fcfefe',
  inactiveLabelColorOnActive = '#cdd1d5',
  activeLabelColor = '#5b5e62',
  activeLabelColorOnHover = '#404346',
  activeLabelColorOnActive = '#010101',
  inactiveTrackColor = '#dce0e3',
  inactiveTrackColorOnHover = '#fcfefe',
  inactiveTrackColorOnActive = '#cdd1d5',
  activeTrackColor = '#404346',
  activeTrackColorOnHover = '#2d2f31',
  activeTrackColorOnActive = '#141516',
  inactiveThumbColor = '#2d2f31',
  activeThumbColor = '#dce0e3',
  focusRingColor = 'rgb(59 130 246 / 0.5)',
  ariaLabel,
}: Props) {
  const isTransitionDisabled = useIsTransitionDisabled(
    size,
    TOGGLE_TRANSITION_DURATION
  );

  return (
    <StyledDarkModeToggle
      size={size}
      inactiveLabelColor={inactiveLabelColor}
      inactiveLabelColorOnHover={inactiveLabelColorOnHover}
      inactiveLabelColorOnActive={inactiveLabelColorOnActive}
      activeLabelColor={activeLabelColor}
      activeLabelColorOnHover={activeLabelColorOnHover}
      activeLabelColorOnActive={activeLabelColorOnActive}
      inactiveTrackColor={inactiveTrackColor}
      inactiveTrackColorOnHover={inactiveTrackColorOnHover}
      inactiveTrackColorOnActive={inactiveTrackColorOnActive}
      activeTrackColor={activeTrackColor}
      activeTrackColorOnHover={activeTrackColorOnHover}
      activeTrackColorOnActive={activeTrackColorOnActive}
      inactiveThumbColor={inactiveThumbColor}
      activeThumbColor={activeThumbColor}
      focusRingColor={focusRingColor}
      data-transition-disabled={isTransitionDisabled}
      data-checked={mode === 'light'}
      data-testid="dark-mode-toggle"
    >
      {dark && (
        <span
          onClick={() => {
            onChange?.('dark');
          }}
          aria-hidden="true"
        >
          {dark}
        </span>
      )}
      <input
        type="checkbox"
        role="switch"
        checked={mode === 'light'}
        aria-label={ariaLabel}
        onChange={(event: React.ChangeEvent<HTMLInputElement>) => {
          onChange?.(event.target.checked ? 'light' : 'dark');
        }}
      />
      {light && (
        <span
          onClick={() => {
            onChange?.('light');
          }}
          aria-hidden="true"
        >
          {light}
        </span>
      )}
    </StyledDarkModeToggle>
  );
}
import { useEffect, useRef, useState } from 'react';
import { useFirstMountState } from './useFirstMountState';
import { usePrevious } from './usePrevious';

export function useIsTransitionDisabled<T>(
  state: T,
  defaultTransitionDurationInMs: number
): boolean {
  const isFirstMount = useFirstMountState();
  const previousState = usePrevious(state);
  const [isTransitionDisabled, setIsTransitionDisabled] =
    useState<boolean>(false);
  const timeoutId = useRef<ReturnType<typeof setTimeout>>();

  useEffect(() => {
    if (state !== previousState) {
      setIsTransitionDisabled(true);
      if (timeoutId.current) {
        clearTimeout(timeoutId.current);
      }
      timeoutId.current = setTimeout(() => {
        setIsTransitionDisabled(false);
        timeoutId.current = undefined;
      }, defaultTransitionDurationInMs);
    }
  }, [state, previousState, defaultTransitionDurationInMs]);

  if (isFirstMount) {
    return true;
  }

  return isTransitionDisabled;
}
import { useRef } from 'react';

export function useFirstMountState(): boolean {
  const isFirst = useRef(true);

  if (isFirst.current) {
    isFirst.current = false;
    return true;
  }

  return isFirst.current;
}
import { useEffect, useRef } from 'react';

export function usePrevious<T>(state: T): T | undefined {
  const ref = useRef<T>();

  useEffect(() => {
    ref.current = state;
  });

  return ref.current;
}
import styled from '@emotion/styled';
import type { Size, StyledProps } from './DarkModeToggle';

export const TOGGLE_TRANSITION_DURATION = 250;

function sizeToScale(size: Size): number {
  switch (size) {
    case 'sm':
      return 1;
    case 'md':
      return 1.5;
    case 'lg':
      return 2;
  }
}

export const StyledDarkModeToggle = styled.div<Required<StyledProps>>`
  display: inline-flex;
  align-items: center;
  user-select: none;
  -webkit-tap-highlight-color: transparent;

  span {
    transition: color ${TOGGLE_TRANSITION_DURATION}ms ease-in-out;
    padding: ${(props) => 8 * sizeToScale(props.size)}px
      ${(props) => 12 * sizeToScale(props.size)}px;
    cursor: pointer;
    font-size: ${(props) => 12 * sizeToScale(props.size)}px;
    font-weight: 600;
    text-transform: uppercase;

    &:nth-of-type(1) {
      &:hover {
        color: ${(props) => props.activeLabelColorOnHover};
      }

      &:active {
        color: ${(props) => props.activeLabelColorOnActive};
      }
    }

    &:nth-of-type(2) {
      &:hover {
        color: ${(props) => props.inactiveLabelColorOnHover};
      }

      &:active {
        color: ${(props) => props.inactiveLabelColorOnActive};
      }
    }
  }

  input {
    position: relative;
    overflow: hidden;
    transition: all ${TOGGLE_TRANSITION_DURATION}ms ease-in-out;
    transition-property: box-shadow, background-color;
    appearance: none;
    margin: 0;
    outline: none;
    border-radius: ${(props) => 14 * sizeToScale(props.size)}px;
    width: ${(props) => 48 * sizeToScale(props.size)}px;
    height: ${(props) => 28 * sizeToScale(props.size)}px;
    background-color: ${(props) => props.inactiveTrackColor};
    cursor: pointer;
    user-select: none;

    &:checked {
      background-color: ${(props) => props.activeTrackColor};

      &:hover,
      &:hover::after {
        background-color: ${(props) => props.activeTrackColorOnHover};
      }

      &:active,
      &:active::after {
        background-color: ${(props) => props.activeTrackColorOnActive};
      }

      &::before {
        left: ${(props) => 24 * sizeToScale(props.size)}px;
        background-color: ${(props) => props.activeThumbColor};
      }

      &::after {
        top: ${(props) => 12 * sizeToScale(props.size)}px;
        right: ${(props) => 2 * sizeToScale(props.size)}px;
        width: ${(props) => 1 * sizeToScale(props.size)}px;
        height: ${(props) => 1 * sizeToScale(props.size)}px;
        border-radius: ${(props) => 0.5 * sizeToScale(props.size)}px;
        background-color: ${(props) => props.activeTrackColor};
      }
    }

    &:hover,
    &:hover::after {
      background-color: ${(props) => props.inactiveTrackColorOnHover};
    }

    &:focus,
    &:focus-visible {
      box-shadow: 0 0 0 ${(props) => 2 * sizeToScale(props.size)}px
        ${(props) => props.focusRingColor};
    }

    &:focus:not(:focus-visible) {
      box-shadow: none;
    }

    &:active,
    &:active::after {
      background-color: ${(props) => props.inactiveTrackColorOnActive};
    }

    &::before {
      content: '';
      position: absolute;
      top: ${(props) => 4 * sizeToScale(props.size)}px;
      left: ${(props) => 4 * sizeToScale(props.size)}px;
      display: block;
      transition: all ${TOGGLE_TRANSITION_DURATION}ms ease-in-out;
      transition-property: left, background-color;
      width: ${(props) => 20 * sizeToScale(props.size)}px;
      height: ${(props) => 20 * sizeToScale(props.size)}px;
      border-radius: ${(props) => 12 * sizeToScale(props.size)}px;
      background-color: ${(props) => props.inactiveThumbColor};
    }

    &::after {
      content: '';
      position: absolute;
      top: ${(props) => -2 * sizeToScale(props.size)}px;
      right: ${(props) => 2 * sizeToScale(props.size)}px;
      display: block;
      transition: all ${TOGGLE_TRANSITION_DURATION}ms ease-in-out;
      transition-property: top, right, width, height, border-radius,
        background-color;
      width: ${(props) => 32 * sizeToScale(props.size)}px;
      height: ${(props) => 32 * sizeToScale(props.size)}px;
      border-radius: ${(props) => 16 * sizeToScale(props.size)}px;
      background-color: ${(props) => props.inactiveTrackColor};
    }
  }

  &[data-checked='false'] {
    span {
      color: ${(props) => props.inactiveLabelColor};

      &:nth-of-type(1) {
        pointer-events: none;
      }
    }
  }

  &[data-checked='true'] {
    span {
      color: ${(props) => props.activeLabelColor};

      &:nth-of-type(2) {
        pointer-events: none;
      }
    }
  }

  &[data-transition-disabled='true'] {
    *,
    *::before,
    *::after {
      transition-duration: 0ms;
    }
  }
`;
