import React, { useRef } from 'react';
import "./Contact.css";
import emailjs from 'emailjs-com';
const Contact = () => {
    const form=useRef();
    const sendemail = (e) =>{
        e.preventDefault();
        emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_USER_ID')
      .then((result) => {
          console.log(result.text);
          alert('Message sent successfully!');
      }, (error) => {
          console.log(error.text);
          alert('Failed to send message, please try again.');
      });

        e.target.reset();
    };
  
    return (
    <section className='contact container section' id='contact'>
        <h2 className='section_title'>Get in Touch</h2>
        <div className='contact_container grid'>
            <h3 className='contact_title'>Let's talk about Everything</h3>
            <p className='contact_details'>Don't like forms? Send me an email ðŸ‘‹</p>
        </div>
        <form ref={form} onSubmit={sendemail} className='contact_form'>
            <div className='contact_form-group'>
                <div className='contact_form-div'>
                    <input type="text" className='contact_form-input' placeholder='Insert your name' />
                </div>
                <div className='contact_form-div'>
                    <input type="email" className='contact_form-input' placeholder='Insert your email' />
                </div>
                <div className='contact_form-div'>
                    <input type="text" className='contact_form-input' placeholder='Insert your subject' />
                </div>
                <div className='contact_form-div'>
                    <textarea name="" id="" cols="30" rows="10" className='contact_form-area' placeholder="Write your message" />
                </div>
                <button className='btn'>Send Message</button>
            </div>
        </form>
    </section>
  )
}

export default Contact
